<?phpclass ControllerBlacklist extends Controller {    function index(){		$this->data['phones'] = $this->model->getPhones();		$this->view->generatePage('Blacklist.php', 'DefaultPage.php', $this->data);	}	function add(){		$this->model->addToBlacklist(Tools::getValue('value'));		Tools::redirectLink($_SERVER['HTTP_REFERER']);	}	function remove(){		$where = 'WHERE phone = "'.Tools::getValue('value').'"';		$this->model->removeFromBlacklist($where);		Tools::redirectLink($_SERVER['HTTP_REFERER']);	}}